import { Component } from '@angular/core';
import {MatTableDataSource} from "@angular/material/table";
import {AfterViewInit, ViewChild} from '@angular/core';
import {MatPaginator, MatPaginatorModule} from '@angular/material/paginator';
import {HttpClient} from "@angular/common/http";
import {Invoice} from "../dto/invoice";

@Component({
  selector: 'app-invoice',
  templateUrl: './invoice.component.html',
  styleUrls: ['./invoice.component.scss']
})
export class InvoiceComponent {
  displayedColumns: string[] = ['ID', 'Sender', 'Receiver', 'Total Amount','Total Paid Amount'];

  invoiceList:Array<Invoice>=[];
  dataSource = new MatTableDataSource<Invoice>(this.invoiceList);
  // @ViewChild(MatPaginator) paginator: MatPaginator;


  constructor(private http:HttpClient) {
    http.get<Array<Invoice>>('api/v1/invoices/').subscribe(elm=>{
      this.invoiceList=elm;
    })
  }

}
export interface PeriodicElement  {
  name: string;
  position: number;
  weight: number;
  symbol: string;
}

