import { Component, OnInit } from '@angular/core';
import {Detail} from "../dto/detail";
import {Invoice} from "../dto/invoice";
import {TempServiceService} from "../service/temp-service.service";
import * as Big from "big.js";


@Component({
  selector: 'app-detail',
  templateUrl: './detail.component.html',
  styleUrls: ['./detail.component.scss']
})
export class DetailComponent{

  detailList:Array<Detail>=[];

  sender:string='';

  receiver:string='';

  flag:boolean;



  //use the service to get the data from invoice component
  constructor(private service:TempServiceService) {
    this.detailList=service.detailList;
    this.sender=service.sender;
    this.receiver=service.receiver;
    this.flag=service.flag;

    console.log(this.sender)
    console.log(this.receiver)
    console.log(this.detailList)
    console.log("hello")
  }

}
<h1>Details</h1>

<main class="p-3">
  <table class="table table-hover table-bordered">
    <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Sender</th>
      <th scope="col">Receiver</th>
      <th scope="col">Total Amount</th>
      <th scope="col">Paid Amount</th>
    </tr>
    </thead>
    <tbody *ngFor="let detail of detailList">
    <tr>
      <th scope="row">{{detail.detailId}}</th>
      <td>{{sender}}</td>
      <td>{{receiver}}</td>
      <td>{{totalAmount}}</td>
      <td>{{detail.paidAmount}}
      </td>
    </tr>

    </tbody>
    <tfoot *ngIf="flag">
    <tr class="text-center">
      <td colspan="5">
         No details Exist
      </td>
    </tr>
    </tfoot>
  </table>
</main>
